import { Component, OnInit, Renderer2 } from '@angular/core';

@Component({
  selector: 'app-courses-student',
  templateUrl: './courses-student.component.html',
  styleUrls: ['./courses-student.component.css']
})
export class CoursesStudentComponent {
  myCourses = [
    { title: 'SMM Expert', nextLesson: 'Next Lesson: 08.07.2024', completion: 86, image: 'smm-expert.png' },
    { title: 'Web Design Basic', nextLesson: 'Next Lesson: 06.07.2024', completion: 50, image: 'web-design-basic.png' },
    { title: 'Motion Design', nextLesson: 'Course Passed', completion: 100, image: 'motion-design.png' },
    { title: 'Photoshop', nextLesson: 'Course Passed', completion: 100, image: 'photoshop.png' },
  ];

  recommendedCourses = [
    { title: 'UI/UX Design Pro', nextLesson: '32 Lessons', completion: 0, image: 'ui-ux-design-pro.png' },
    { title: 'SMM Pro', nextLesson: '23 Lessons', completion: 0, image: 'smm-pro.png' },
    { title: 'Motion Design Pro', nextLesson: '16 Lessons', completion: 0, image: 'motion-design-pro.png' },
  ];
  constructor(private renderer: Renderer2) {}

  ngOnInit(): void {
    this.initializeSidebarScript();
  }

  initializeSidebarScript() {
    const body = document.querySelector('body') as HTMLElement;
    const sidebar = body.querySelector('nav.sidebar') as HTMLElement;
    const toggle = body.querySelector('.toggle') as HTMLElement;
    const searchBtn = body.querySelector('.search-box') as HTMLElement;
    const modeSwitch = body.querySelector('.toggle-switch') as HTMLElement;
    const modeText = body.querySelector('.mode-text') as HTMLElement;

    toggle.addEventListener('click', () => {
      sidebar.classList.toggle('close');
    });

    searchBtn.addEventListener('click', () => {
      sidebar.classList.remove('close');
    });

    modeSwitch.addEventListener('click', () => {
      body.classList.toggle('dark');

      if (body.classList.contains('dark')) {
        modeText.innerText = 'Light mode';
      } else {
        modeText.innerText = 'Dark mode';
      }
    });
  }
}
